function run() {
  var go = GeneOntology;
  var pro = ProteinOntology;
  var terms = termgenie.getTerms("unit", pro);
  var label = "";
  var mdefString = 'GO_0043234';
  for(var i=0; i<terms.length; i+= 1) {
    var check = termgenie.checkGenus(terms[i], "PR:000000001", pro);
    if (check.isGenus() !== true) {
      termgenie.error(check.error());
      return;
    }
    if (i > 1) {
      label += "-"
    }
    label += termgenie.name(terms[i]);
    mdefString += ' and (has_part some ?P )'+i;
  }
  label += " complex";
  var definition = termgenie.definition("Any protein complex consisting of ",terms, pro, ", ",".");
  var synonyms = termgenie.synonyms(null, terms, pro, "-", " complex", label);
  var mdef = termgenie.createMDef(mdefString);
  for(i=0; i<terms.length; i+= 1) {
    mdef.addParameter('P'+i, terms[i], pro);
  }
  termgenie.createTerm(label, definition, synonyms, mdef);
};


function run() {
  var a = termgenie.getSingleTerm("target", FMA);
  if (a === null) {
    termgenie.error("The specified term does not correspond to the pattern");
    return;
  }
  var label = "Abnormal "+termgenie.name(a, FMA) + " morphology";
  var definition = "Any morphological abnormality of a "+termgenie.name(a, FMA)+".";
  var synonyms = null; // TODO
  var mdef = termgenie.createMDef('PATO_0000051 and ?R some ?X');
  mdef.addParameter('X', a, FMA);
  mdef.addRelationByName('R', 'OBO_REL:inheres_in', go);
  termgenie.createTerm(label, definition, synonyms, mdef);
};


function run() {
  var e = termgenie.getSingleTerm("entity", FMA);
  var q = termgenie.getSingleTerm("quality", PATO);
  if (e === null || q === null) {
    termgenie.error("The specified terms do not correspond to the pattern");
    return;
  }
  var label = termgenie.name(q, PATO) + " " + termgenie.name(e, FMA);
  var definition = "Any "+termgenie.name(e, FMA)+" that is "+termgenie.name(q, PATO)+".";
  var synonyms = null; // TODO
  var mdef = termgenie.createMDef('?Q and ?R some ?E');
  mdef.addParameter('Q', q, PATO);
  mdef.addParameter('E', e, FMA);
  mdef.addRelationByName('R', 'OBO_REL:inheres_in', go);
  termgenie.createTerm(label, definition, synonyms, mdef);
};


function run() {
  var go = GeneOntology;
  var e = termgenie.getSingleTerm("entity", go);
  var q = termgenie.getSingleTerm("quality", PATO);
  if (e == null ||  q == null) {
    // check branch
    termgenie.error("The specified terms do not correspond to the pattern");
    return;
  }
  var label = termgenie.name(q, PATO) + " of " + termgenie.name(e, go);
  var definition = "Any "+termgenie.name(q, PATO)+" of "+termgenie.name(e, go)+".";
  var synonyms = null; // TODO
  var mdef = termgenie.createMDef('?Q and ?R some ?E');
  mdef.addParameter('Q', q, PATO);
  mdef.addParameter('E', e, go);
  mdef.addRelationByName('R', 'OBO_REL:inheres_in', go);
  termgenie.createTerm(label, definition, synonyms, mdef);
};


function run() {
  var c = termgenie.getSingleTerm("cell", CL);
  var a = termgenie.getSingleTerm("location", Uberon);
  if (c == null || a == null) {
    // check branch
    termgenie.error("The specified terms do not correspond to the pattern");
    return;
  }
  var label = termgenie.name(a, Uberon) + " " + termgenie.name(c, CL);
  var definition = "Any "+termgenie.name(c, CL)+" that is part of a "+termgenie.name(a, Uberon)+".";
  var synonyms = null; // TODO
  var mdef = termgenie.createMDef('?C and part_of some ?A');
  mdef.addParameter('C', c, CL);
  mdef.addParameter('A', a, Uberon);
  termgenie.createTerm(label, definition, synonyms, mdef);
};


function run() {
  var go = GeneOntology;
  var pro = ProteinOntology;
  var c = termgenie.getSingleTerm("cell", CL);
  var two = [pro, go];
  var p = termgenie.getSingleTerm("membrane_part", two);
  if (c == null || p == null || !(termgenie.genus(p, "GO:0032991", go) || termgenie.genus(p, "PR:000000001", pro))) {
    // check branch
    termgenie.error("The specified terms do not correspond to the pattern");
    return;
  }
  var label = termgenie.name(p, two) + " " + termgenie.name(c, CL);
  var definition = "Any "+termgenie.name(c, CL)+" that has "+termgenie.name(p, two)+" on the plasma membrane.";
  var synonyms = null; // TODO
  var mdef = termgenie.createMDef('?C and has_plasma_membrane_part some ?P');
  mdef.addParameter('C', c, CL);
  mdef.addParameter('P', p, two);
  // cdef.property("multivalued(has_plasma_membrane_part)");
  // cdef.property("any_cardinality(has_plasma_membrane_part)");
  termgenie.createTerm(label, definition, synonyms, mdef);
};


